CREATE TABLE room_type (id integer NOT NULL PRIMARY KEY, room_type varchar(50) NOT NULL UNIQUE, price integer, details varchar(50) NOT NULL );
CREATE TABLE room_info (id integer NOT NULL PRIMARY KEY AUTO_INCREMENT, room_number integer NOT NULL UNIQUE, room_type_id integer  NOT NULL REFERENCES room_type (id), created timestamp default CURRENT_TIMESTAMP);
CREATE TABLE user_info (id integer NOT NULL PRIMARY KEY AUTO_INCREMENT, first_name varchar(20) NOT NULL, last_name varchar(20) NOT NULL, address varchar(100) NOT NULL, email varchar(20) NOT NULL);
CREATE TABLE reservation_details (id integer NOT NULL PRIMARY KEY AUTO_INCREMENT, confirmation_code varchar(50) NOT NULL, user_id integer  NOT NULL REFERENCES user_info (id), reserved_on_date timestamp default CURRENT_TIMESTAMP, booking_for_date date NOT NULL, room_number integer  NOT NULL REFERENCES room_info (room_number), status integer NOT NULL);